import { Network } from "../components/Network";
import {action, makeObservable, observable} from 'mobx';

class NoteStore {
    constructor() {
        this.title = '';
        this.id = 'PzBEXwf47QMGQltedMfpUJ0F21PecGKFtgzsF0oF9oL9QXhVTXFD55ANx3P4iNQo';
        this.listOfItems = ["TEST"];

            makeObservable(this, {
                title: observable,
                id: observable,
                listOfItems: observable,
                addNewNote: action.bound,
                setTitle: action.bound, 
                setListOfItems: action.bound,
                edit: action.bound,
                delete: action.bound,
            })
    }

    setListOfItems() {
        Network('tasks?access_token=', this.id, 'GET')
        .then(result => {
            this.listOfItems = result
            console.log("проверка", this.listOfItems.map(item => item.title));
          })
    }

    setTitle(text) {
        this.title = text.target.value; 
    }

    addNewNote() {
        console.log("содержание в поле title", this.title);

        if(!this.title || this.title == '' || this.title == ' ') {
            this.setListOfItems()
            alert("Поле с заметкой пустое")
        } else {
            Network('tasks?access_token=', this.id, 'POST', {title: this.title});
            setTimeout(this.setListOfItems, 200);
            this.title = ''
        }  
    }

    edit(item) {
        this.title = prompt("Edit Note", item.title)
        this.setTitle(this.title)
        setTimeout(this.setListOfItems, 200);
        this.setListOfItems();
    }


    delete(id) {
        Network(`tasks/${id}?access_token=`, this.id, 'DELETE');
        this.setListOfItems()
    }
}

export const StoreTodo = new NoteStore();
