import {action, makeObservable, observable} from 'mobx';
import {Network} from "../components/Network"
import { StoreTodo } from './NoteStore';

class AuthStore {
    constructor() {
        this.email = '';
        this.password = '';
        this.response =  '';
        this.token = '';
        this.id = '';
            makeObservable(this, {
                response: observable,
                email: observable,
                password: observable,
                token: observable,
                id: observable,
                setEmail: action.bound,
                setPassword: action.bound,
                delEmail: action.bound,
                delPassword: action.bound,
                sendAuthDate: action.bound,
            })
    }

    setEmail(e) {
        this.email = e.target.value
        console.log("email", this.email);
    }

    setPassword(e) {
        this.password = e.target.value
        console.log("password", this.password);
    }

    delEmail() {
        this.email = ""
    }

    delPassword() {
        this.password = ""
    }

    sendAuthDate(url, method, page) {
        console.log(url, "url rbrtbrtb");

        if(this.email && this.password !=='') {
                Network(url,' ', method, {
                  email: this.email,
                  password: this.password,
                }).then(response => {
                    console.log("id1", this.id);
                  if (response.id) {
                    this.response = response;
                    this.token = response.id;
                    this.id = response.user
                    alert("Пользователь авторизован:" + this.token);
                    console.log("id2", this.id);
                  } else alert("Ошибка, попробуте ввести повторно данные");
                })
                .catch((err) => console.log(err))
            }
            // if(url == "Users/logout?") {
            //     console.log(this.id, "Idhaaaaaaaaaaa");
            //     Network(url, StoreTodo.id, method)
            // }
        else alert("Заполните все поля");
        this.delPassword()
    }
}

export const store = new AuthStore();
