class AuthStore {
  constructor() {
    this.email = "";
    this.password = "";
    this.response = "";
    this.token = "";
    this.id = "";
    makeObservable(this, {
      response: observable,
      email: observable,
      password: observable,
      token: observable,
      id: observable,
