import {action, makeObservable, observable} from 'mobx';
import {Network} from "../components/Network"

class AuthStore {
    constructor() {
        (this.email = ''),
        (this.password = ''),
        (this.response =  ''),
            makeObservable(this, {
                response: observable,
                email: observable,
                password: observable,
                setEmail: action.bound,
                setPassword: action.bound,
                delEmail: action.bound,
                delPassword: action.bound,
                sendAuthDate: action.bound,
            })
    }

    setEmail(e) {
        this.email = e.target.value
        console.log(this.email);
    }

    setPassword(e) {
        this.password = e.target.value
        console.log(this.email);
    }

    delEmail() {
        this.email = ""
    }

    delPassword() {
        this.password = ""
    }

    sendAuthDate(url, method, page) {
        if(this.email && this.password !=='') {
            try {
                Network(url,' ', method, {
                  email: this.email,
                  password: this.password,
                }).then(response => {
                  if (response.id) {
                    this.response = response.id;
                    alert("Пользователь авторизован:" + this.response);
                    StoreToDo.id = this.response;
                    // Временно убираем проверку
                    // if (this.response !== '') navigation.navigate(page);
                  } else
                  alert("Ошибка, возможно пользователь не существует");
                });
              } catch (error) {
                console.log('error', error);
              }
        } else alert("Заполните все поля");
        this.delPassword()
    }
}

export const store = new AuthStore();
