import {action, makeObservable, observable} from 'mobx';

class AuthStore {
    constructor() {
        this.email = '',
        this.password = '',
        this.response = '',
            makeObservable(this, {
                response: observable,
                email: observable,
                password: observable,
                setEmail: action.bound,
                setPassword: action.bound,
            })
    }

    setEmail(email) {
        this.email = email

    }

    setPassword(password) {
        this.password = password
    }


}

export const store = new AuthStore();
