import { observer } from "mobx-react";
import React from "react";
import "../../styles/RegistrationPage.css";
import { store } from "../stores/AuthStore"
import { Link } from "react-router-dom";

import { Registration } from "./RegistrationPage"
import ListPage, { List } from "./ListPage"
import { StoreTodo } from "../stores/NoteStore";

const Auth = observer(() => {
    return (
      <form>

        <div className="RegistrationPage">
          <div className="RegistrationHeader">
            <p>
              Авторизация 
            </p>         
          </div>
          <div className="MainWindowRegistration">
            <div>
              <p>
                Enter your Email:
              </p>
              <input className="Input"
                type="text"
                value={store.email}
                onChange={store.setEmail}
                placeholder="Enter email..."
                />
            </div>
            <div>
              <p>
                Enter your Password:
              </p>
              <input className="Input"
                type="text"
                value={store.password}
                onChange={store.setPassword}
                placeholder="Enter password..."
                />
            </div>

              {/* <Link to={store.token || store.email && store.password ? "/ListPage" : '/errorPage'}> */}
                <button type="button" onClick={() => {

                  store.sendAuthDate('Users/login', 'POST', '')
                  // StoreTodo.setListOfItems()
                  } }>Accept

                  <Link to={store.token ? "/ListPage" : '/errorPage'}></Link>
                </button>
              {/* </Link> */}

              <Link to="/registration">
                <button type="button">Registration
                </button>
              </Link>
          </div> 
        </div>
      </form>
    )
})

export default Auth;
