import {action, makeObservable, observable} from 'mobx';

class AuthStore {
    constructor() {
        (this.ema),
        (this.password = ''),
        (this.response =  ''),
            makeObservable(this, {
                response: observable,
                email: observable,
                password: observable,
                setEmail: action.bound,
                setPassword: action.bound,
            })
    }

    setEmail(text) {
        this.email = text
    }

    setPassword(text) {
        this.password = text
    }
}

export const store = new AuthStore();
